 EQUS "PA2 "
 EQUS "SH IH TT1 "
 EQUS "PA5 "
 EQUS "IH TT1 SS "
 EQUS "PA4 "
 EQUS "A "
 EQUS "PA4 "
 EQUS "MM IS "
 EQUS "PA2 "
 EQUS "JH UW1 MM PP"
 EQUB 13

ENDIF

