\ ******************************************************************************
\       Name: HALL
\ ******************************************************************************

.HALL

 LDA #0
 JSR DOVDU19
 JSR UNWISE
 LDA #0
 JSR TT66
 JSR DORND
 BPL HA7
 AND #3
 STA T
 ASL A
 ASL A
 ASL A
 ADC T
 TAX
 LDY #3
 STY CNT2

.HAL8

 LDY #2

.HAL9

 LDA HATB,X
 STA XX15,Y
 INX
 DEY
 BPL HAL9
 TXA
 PHA
 JSR HAS1
 PLA
 TAX
 DEC CNT2
